import React, { useState } from 'react';
import styled, { css } from 'styled-components';
import CurrentState from '../components/CurrentState';
import backgroundImg from '../data/img/select_bg.png';
import rewordTitle from '../data/img/reword_title.png';
import joyPink from '../data/img/joy_pink.png';
import joyWhite from '../data/img/joy_white.png';
import fundingImg1 from '../data/img/funding_img1.png';

const SelectPage = () => {

    const [check, setCheck] = useState(false);

    const handleClick = (e) => {
        setCheck(!check);
