import React from 'react';
import styled from 'styled-components';
import titleImgMini from '../data/img/main_title_mini.png';
import backgroundImg from '../data/img/main_bg.png';

const MainPage = () => {
    return (
        <BackgroundWrapper>
            <TopWrapper>
                <TopLeftWrapper>
                    <img src={titleImgMini} alt={titleImgMini}/>
                    <MainTextStyle>
                    2020년 한 해 동안 여러 만남을 갖고 더치페이를 하면서 어떤 감정을 느끼셨나요?<br/>
                    기쁨, 다정함, 감사함, 불편했던 감정들 중 당신이 느꼈던 감정을 나누어주세요.<br/>
                    다정이로 디자인된 ‘QR테이블 부착 스티커’, ‘뱃지’ 로 구성된 다정 패키지를 보내드립니다.
                    </MainTextStyle>
                </TopLeftWrapper>
                <TopRightWrapper>
                    <ul>
                        <ListStyle>
                            <p>60</p>
                            <span>후원자</span>
                        </ListStyle>
                        <ListStyle>
                            <p>30</p>
                            <span>남은 기간</span>
                        </ListStyle>
                        <ListStyle>
                            <p>65%</p>
                            <span>전체 펀딩 현황</span>
                        </ListStyle>
                    </ul>
                </TopRightWrapper>
            </TopWrapper>
        </BackgroundWrapper>
    )
}

const BackgroundWrapper = styled.div`
    background-image: url(${backgroundImg});
    width : 1920px;
    height : 1080px;
    top : 0;
    position : absolute;
`

const TopWrapper = styled.div`
    width : 1200px;
    border : 1px solid blue;
    margin : 129px auto 0;
    display: flex;
`

const TopLeftWrapper = styled.div`
    border : 1px solid green;
    flex : 1.5;
`

const TopRightWrapper = styled.div`
    border : 1px solid pink;
    width : 400px;
    flex : 1;
    float : right;
`

const MainTextStyle = styled.p`
    font-size : 16px;
    line-height : 22px;
    letter-spacing : -0.48px;
    margin-top:30px;
    width : 650px;
`

const ListStyle = styled.li`
    border : 1px solid red;
    float : left;
    width : 160px;
    text-align : center;
    height : 100px;

    & > p {
        font-size : 45px;
        margin : 0;
        line-height : 45px;
        color : #ff6464;
    }

    & > span {
        font-size : 14px;
    }
`

export default MainPage;
