import React, { useState } from 'react';
import styled, { css } from 'styled-components';
import joyPink from '../data/img/joy_pink.png';
import joyWhite from '../data/img/joy_white.png';
import fundingImg1 from '../data/img/funding_img1.png';
import kindPink from '../data/img/kind_pink.png';
import kindWhite from '../data/img/kind_white.png';
import fundingImg2 from '../data/img/funding_img2.png';

const SelectCard = (props) => {

    const [check, setCheck] = useState(false);

    const handleClick = (e) => {
        if (props.id === "joy"){
            setCheck(!check);
        } else if (props.id === "kind") {
            setCheck(!check);
        }
        console.log(check, props.id);
    }

    if (props.id === "joy"){
        return (
            <SelectBox onClick={handleClick} check={check}>
                <SelectBox_Left>
                    <img src={check ? joyWhite : joyPink}/>
                    <SelectText check={check}>당신이 '기쁨'을 느꼈다면 기쁜 감정을 펀딩 해주세요</SelectText>
                </SelectBox_Left>
                <SelectBox_Right>
                    <img src={fundingImg1}/>
                </SelectBox_Right>
            </SelectBox>
        )
    } else if (props.id === "kind") {
        return (
            <SelectBox onClick={handleClick} check={check}>
                <SelectBox_Left>
                    <img src={check ? joyWhite : joyPink}/>
                    <SelectText check={check}>흠</SelectText>
                </SelectBox_Left>
                <SelectBox_Right>
                    <img src={fundingImg1}/>
                </SelectBox_Right>
            </SelectBox>
        )
    }
}

const SelectBox = styled.div`
    width : 750px;
    height : 130px;
    position : relative;
    background-color : white;
    border : 3px solid white;
    border-radius : 16px;
    box-shadow : 0 0 10px 0 rgba(0, 0, 0, 0.05);
    transition : border 0.1s ease-in-out;
    transition:background-color 0.2s ease-in-out;
    cursor: pointer;

    &:hover {
        border : 3px solid #ff6464;
    }

    ${props =>
    props.check &&
    css`
      background : #ff7b7b;
      border : 3px solid #ff6464;
    `}
`

const SelectBox_Left = styled.div`
    border : 1px solid pink;
    width : 460px;
    margin : 28px 0 0 49px;
    font-size : 18px;
`

const SelectBox_Right = styled.div`
    border : 1px solid blue;
    width : 219px;
    height : 180px;
    position : absolute;
    bottom : -3px;
    right : 18px;
`

const SelectText = styled.p`
    margin : 5px 0 0 0;
    color : #333;

    ${props =>
    props.check &&
    css`
      color : white;
    `}
`

export default SelectCard;
