import React, { useState } from 'react';
import styled, { css } from 'styled-components';
import CurrentState from '../components/CurrentState';
import backgroundImg from '../data/img/select_bg.png';
import rewordTitle from '../data/img/reword_title.png';
import joyPink from '../data/img/joy_pink.png';

const SelectPage = () => {

    const [check, setCheck] = useState(false);
    const onToggle = () => {
        setCheck(!check);
        
    console.log(check);
    }

    return (
        <BackgroundWrapper>
            <TopWrapper>
                <TopLeftWrapper>
                    <img src={rewordTitle} alt={rewordTitle}/>
                    <MainTextStyle>
                    2020년 한 해를 다정하게 채워주웠던 만남들을 리마인드 해보세요.<br/> 만남 속에서 당신은 어떤 페이를 해오셨나요? <br/>기쁨, 다정함, 감사함, 불편했던 감정 들 중 당신이 느꼈던 감정을 나누어주세요.
                    </MainTextStyle>
                </TopLeftWrapper>
                <TopRightWrapper>
                    <CurrentState/>
                </TopRightWrapper>
            </TopWrapper>
            <MiddleWrapper>
                <MiddleLeftWrapper>
                    <SelectBox onClick={onToggle} check={check}>
                        <SelectBox_Left>
                            <img src={joyPink}/>
                        </SelectBox_Left>
                    </SelectBox>
                </MiddleLeftWrapper>
            </MiddleWrapper>
        </BackgroundWrapper>
    )
}

const BackgroundWrapper = styled.div`
    background-image: url(${backgroundImg});
    width : 1920px;
    height : 980px;
    top : 0;
    position : absolute;
`

const TopWrapper = styled.div`
    width : 1200px;
    margin : 100px auto 59px;
    display: flex;
    justify-content : space-between;
`

const TopLeftWrapper = styled.div`
    display : block;
`

const TopRightWrapper = styled.div`
    display : block;
    float : right;
`

const MainTextStyle = styled.p`
    font-size : 16px;
    line-height : 22px;
    letter-spacing : -0.48px;
    margin:30px 0 0 0;
    width : 650px;
`

const MiddleWrapper = styled.div`
    border : 1px solid blue;
    width : 1200px;
    margin : 0 auto;
`

const MiddleLeftWrapper = styled.div`
    border : 1px solid red;
    width : 750px;
`

const SelectBox = styled.div`
    width : 750px;
    height : 130px;
    background-color : white;
    border-radius : 16px;
    border : 3px solid white;
    box-shadow : 0 0 10px 0 rgba(0, 0, 0, 0.05);
    transition:border 0.08s ease-in-out;
    transition:background-color 0.2s ease-in-out;

    &:hover {
        border : 3px solid #ff6464;
    }

    ${props =>
    props.check &&
    css`
      background : #ff7b7b;
      border : 3px solid #ff6464;
    `}
`

const SelectBox_Left = styled.div`
`

export default SelectPage;
