import React, {forwardRef} from 'react';
import styled from 'styled-components';

const CommonButton = forwardRef((title, { onClick, href }, ref) => {
    return (
        <ButtonStyle href={href} onClick={onClick} ref={ref}>{title}</ButtonStyle>
    )
});

const ButtonStyle = styled.button`
    width : 200px;
    height : 60px;
    background-color : #ff6464;
    border : 0;
    border-radius : 50px;
    margin : 100px auto 0;
    display : block;
    color : white;
    font-size : 24px;
    outline : 0;
    box-shadow : 0px 0px 10px rgba(0, 0, 0, 0.1);

    &:active {
        border : 0;
        outline : 0;
    }

    &:hover {
        cursor: pointer;
    }
`

export default CommonButton;
